import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs/internal/Observable';
import { Quotes } from '../models/quotes.model';

@Injectable({
  providedIn: 'root'
})
export class QuotesService {

  constructor(private http: HttpClient) { }
  getAllQuotes(): Observable<any> {
    return this.http.get<Quote[]>('https://localhost:7174/api/Quotes/getQuotes');
  }
  addQuote(quote: Quote) {
    return this.http.post('https://localhost:7082/api/Products/AddProduct', quote);
  }
  getQuote(id: string): Observable<Quote> {
    return this.http.get<Quote>('https://localhost:7082/api/Products/GetProduct/' + id);
  }
  updateQuote(quote: Quote): Observable<Quote> {
    return this.http.post<Quote>('https://localhost:7011/api/EditProduct', quote);
  }
  deleteQuote(id: string): Observable<Quote> {
    return this.http.delete<Quote>('https://localhost:7082/api/Products/DeleteProduct/' + id);
  }
}
